import React from "react";

const DesignerTool = () => {
  const [headerSize, setHeaderSize] = useState(32);
  const [textSize, setTextSize] = useState(24);

  const increaseSize = (type) => {
      if(type==="header"){
          setHeaderSize((headerSize)=>headerSize+8)
      }else{
          setTextSize((textSize)=>textSize+8)
      }
  }
  return (
    <div>
      <h2>Designer Tool</h2>
      <span> Heading Size</span> <button>+</button> <span>FontSize</span>
      <button>-</button>
      <br />
      <span> Text Size</span> <button>+</button> <span>FontSize</span>
      <button>-</button>
      <h3 style={{ fontSize: `${headerSize}px` }}>This is a header</h3>
      <p stylr={{ fontSize: `${textSize}px` }}>This is a text value</p>
    </div>
  );
};

export default DesignerTool;
