import React, { useState } from "react";

const PasswordMatch = () => {
  const [psd, setpsd] = useState("");
  const [cpPsd, setcpPsd] = useState("");

  const changePsdHandler = (e) => {
    let value = e.target.value;
    setpsd(value);
  };
  const changeCpPsdHandler = (e) => {
    let value = e.target.value;
    setcpPsd(value);
  };

  const checkPasswordMatch = () => {
    if (psd != cpPsd) {
      return <div>Passwords do not match</div>;
    }
    return <></>;
  };

  return (
    <>
      <h2>Password Match</h2>
      Password:{" "}
      <input
        type="password"
        value={psd}
        placeholder="Enter Password"
        onChange={changePsdHandler}
      />
      Confirm Password:{" "}
      <input
        type="password"
        placeholder="Re-enter Password"
        onChange={changeCpPsdHandler}
      />
      <checkPasswordMatch />
    </>
  );
};

export default PasswordMatch;
