import { useState } from "react";

export const Counter = () => {
  const [value, valueHandler] = useState(0);

  function increment() {
    valueHandler(value + 1);
  }
  function decrement() {
    if (value > 0) {
      valueHandler(value - 1);
    }
  }
  return (
    <div>
      <h2>Counter</h2>
      <button onClick={increment}>+</button>
      <span style={{ padding: "1rem" }}>{value}</span>
      <button onClick={decrement}>-</button>
    </div>
  );
};
